import React, { useEffect, useState } from "react";
import eventTags from "../eventTags.js"

function PlanDay() {
    const [selected, setSelected] = useState([]);
    const getEvents =()=> {
        var params = selected.join(" ");
        var url = `/getEvents?eventtags=${params}`
        useEffect(()=>{
            fetch(url).then(
                response => response
            )
        })
    }
    const ammendSelected = (tagName) => {
        if (selected.includes(tagName)) {
            const index = selected.indexOf(tagName);
            var copiedSelected = [...selected];
            copiedSelected.splice(index, 1);
            setSelected(copiedSelected);
        }
        else {
            setSelected([...selected, tagName])
        }

        console.log([...selected]);
    }
    return (
        <div style={{paddingLeft: "28px"}}>
            <h2>What are the vibes for today???ðŸ˜‹</h2>
            {eventTags.map((tag) => {
                return (
                    <span className="tagChecker" onClick={() => ammendSelected(tag.tagName)} style={{backgroundColor: selected.includes(tag.tagName) ? 'rgba(213,213,213,0.5)' : 'white'}}>
                        <p>{tag.tagName} {tag.icon}</p>
                    </span>
                )
            })}

            <div class="tagSubmission" onClick={getEvents}><p>SUBMIT</p></div>
        </div>
    )
}

export default PlanDay;
